package javatestv1;

public class TERNARY {

	public static void main(String[] args) {
		// TODO Auto-generated method stub        
	 // Using ternary operator to find the maximum of two numbers
		int a = 20, b = 20;
        int max = (a > b) ? a : b;
        System.out.println("Maximum of " + a + " and " + b + " is: " + max);
	}

}
